        num1, num2 = 25, 30
        result = add_numbers(num1, num2)
        self.assertEqual(result, 55, f'Error, expected 55, but got {result}')

        # Test with zero
        num1, num2 = 0, 10
        result = add_numbers(num1, num2)
        self.assertEqual(result, 10, f'Error, expected 10, but got {result}')

        # Test with negative integers
        num1, num2 = -3, -7
        result = add_numbers(num1, num2)
        self.assertEqual(result, -10, f'Error, expected -10, but got {result}')

        # Test with floating numbers
        num1, num2 = 1.5, 2.5
