# Import the necessary packages.
import unittest
from add import add_numbers

class TestAddNumbers(unittest.TestCase):

    def test_Add_numbers_4beee899f4(self):
        """Test add_numbers with typical inputs."""
        # Test with positive numbers
        self.assertEqual(add_numbers(5, 10), 15)
        
        # Test with negative numbers
        self.assertEqual(add_numbers(-5, -10), -15)

        # Test with zero
        self.assertEqual(add_numbers(5, 0), 5)
        self.assertEqual(add_numbers(0, 5), 5)

        # Test with negative and positive numbers
        self.assertEqual(add_numbers(-5, 10), 5)
        self.assertEqual(add_numbers(5, -10), -5)

        # Test with large numbers
        self.assertEqual(add_numbers(1000000, 2000000), 3000000)

        # Test with small numbers
        self.assertEqual(add_numbers(0.0000001, 0.0000002), 0.0000003)
        
        # Test when the inputs are not int or float (should raise a TypeError)
        with self.assertRaises(TypeError):
            add_numbers("5", 10)

        with self.assertRaises(TypeError):
            add_numbers(5, "10")

if __name__ == '__main__':
    unittest.main()
