import unittest
from calculate import judge_leap_year


class TestcalculateMethods(unittest.TestCase):

    def test_judge_leap_year(self):
        """
        Test that the judge_leap_year function returns correct output
        """
        leap_year = 2000    # As we know 2000 is a leap year
        non_leap_year = 2001  # 2001 is not a leap year

        # asserting for a leap year
        self.assertTrue(judge_leap_year(leap_year))

        # asserting for a non-leap year
        self.assertFalse(judge_leap_year(non_leap_year))


