# Test generated by RoostGPT for test roost-test using AI Type Azure Open AI and AI Model roost-gpt4-32k

import unittest
from calculate import judge_leap_year

class TestCalculate(unittest.TestCase):

    def test_judge_leap_year(self):
        # test for leap year
        leap_year = 2020  
        self.assertEqual(judge_leap_year(leap_year), True)

        # test for non leap year
        non_leap_year = 2021
        self.assertEqual(judge_leap_year(non_leap_year), False)

        # test for century year which is a leap year
        # e.g. 2000, 1600, are leap years because they are divisible by 400
        century_leap_year = 2000 
        self.assertEqual(judge_leap_year(century_leap_year), True)

        # test for century year which is not a leap year
        # e.g. 1900, 1700, are not leap years because they are NOT divisible by 400
        century_non_leap_year = 1900
        self.assertEqual(judge_leap_year(century_non_leap_year), False)

if __name__ == '__main__':
    unittest.main(verbosity=2)
