import unittest
from calculate import month_days

class TestCalculate(unittest.TestCase):
    def test_month_days(self):
        self.assertEqual(month_days(1, True), 31) # January, leap year
        self.assertEqual(month_days(1, False), 31) # January, non-leap year
        self.assertEqual(month_days(4, True), 30) # April, leap year
        self.assertEqual(month_days(4, False), 30) # April, non-leap year
        self.assertEqual(month_days(2, True), 29) # February, leap year
        self.assertEqual(month_days(2, False), 28) # February, non-leap year

if __name__ == "__main__":
    unittest.main(verbosity=2)
