import unittest
from calculate import month_days

class TestMonthDays(unittest.TestCase):
    def setUp(self):
        self.leap_year = True
        self.non_leap_year = False

    def test_month_days_january(self):
        self.assertEqual(month_days(1, self.leap_year), 31)
        self.assertEqual(month_days(1, self.non_leap_year), 31)

    def test_month_days_february_leap_year(self):
        self.assertEqual(month_days(2, self.leap_year), 29)

    def test_month_days_february_non_leap_year(self):
        self.assertEqual(month_days(2, self.non_leap_year), 28)

    def test_month_days_april(self):
        self.assertEqual(month_days(4, self.leap_year), 30)
        self.assertEqual(month_days(4, self.non_leap_year), 30)

    def test_month_days_may(self):
        self.assertEqual(month_days(5, self.leap_year), 31)
        self.assertEqual(month_days(5, self.non_leap_year), 31)

    # TODO: Write similar test cases for the rest of the months

if __name__ == "__main__":
    suite = unittest.TestLoader().loadTestsFromTestCase(TestMonthDays)
    unittest.TextTestRunner(verbosity=2).run(suite)
