# Test generated by RoostGPT for test roost-test using AI Type Open AI and AI Model gpt-4

import calculate
import unittest

class TestCalculateAge(unittest.TestCase):

    def test_month_days(self):
        # Test for non-leap year
        self.assertEqual(calculate.month_days(1, False), 31) # January
        self.assertEqual(calculate.month_days(2, False), 28) # February
        self.assertEqual(calculate.month_days(11, False), 30) # November
        self.assertEqual(calculate.month_days(12, False), 31) # December

        # Test for leap year
        self.assertEqual(calculate.month_days(1, True), 31) # January
        self.assertEqual(calculate.month_days(2, True), 29) # February
        self.assertEqual(calculate.month_days(11, True), 30) # November
        self.assertEqual(calculate.month_days(12, True), 31) # December

if __name__ == '__main__':
    unittest.main()
