import calculate
import unittest

class TestMonthDays(unittest.TestCase):

    def setUp(self):
        pass

    def test_month_days(self):
        # Test February in a leap year
        self.assertEqual(calculate.month_days(2, True), 29)

        # Test February in a non-leap year
        self.assertEqual(calculate.month_days(2, False), 28)

        # Test April, June, September and November (30 day months)
        for month in [4, 6, 9, 11]:
            self.assertEqual(calculate.month_days(month, False), 30)

        # Test January, March, May, July, August, October and December (31 day months)
        for month in [1, 3, 5, 7, 8, 10, 12]:
            self.assertEqual(calculate.month_days(month, False), 31)

